// Method 1: Using Regular Expression (Recommended)
function extractLocationId(gmbPath) {
  const regex = /locations\/(\d+)/;
  const match = gmbPath.match(regex);

  if (match) {
    return `locations/${match[1]}`;
  } else {
    throw new Error('Location ID not found in the provided path');
  }
}

// Method 2: Using String Split
function extractLocationIdSplit(gmbPath) {
  const parts = gmbPath.split('/');
  const locationIndex = parts.indexOf('locations');

  if (locationIndex !== -1 && locationIndex + 1 < parts.length) {
    return `locations/${parts[locationIndex + 1]}`;
  } else {
    throw new Error('Location ID not found in the provided path');
  }
}

