// n8n Code node (JavaScript)
// Returns one item with an array of all Saturdays in the month
// Each Saturday is a date string (YYYY-MM-DD)

function pad(n) { return String(n).padStart(2, '0'); }

const input = (items && items[0] && items[0].json) ? items[0].json : {};
const timeZone = input.timeZone || 'Asia/Kolkata';
const monthOffset = Number.isInteger(input.monthOffset) ? input.monthOffset : 0;

function nowInTZ(tz) {
  return new Date(new Date().toLocaleString('en-US', { timeZone: tz }));
}

const nowTZ = nowInTZ(timeZone);
let targetYear = nowTZ.getFullYear();
let targetMonth = nowTZ.getMonth() + monthOffset;

if (targetMonth < 0 || targetMonth > 11) {
  targetYear += Math.floor(targetMonth / 12);
  targetMonth = ((targetMonth % 12) + 12) % 12;
}

const daysInMonth = new Date(targetYear, targetMonth + 1, 0).getDate();

const weekdayFmt = new Intl.DateTimeFormat('en-US', { weekday: 'long', timeZone });
const monthNameFmt = new Intl.DateTimeFormat('en-US', { month: 'long', timeZone });
const monthName = monthNameFmt.format(new Date(Date.UTC(targetYear, targetMonth, 15)));

const saturdays = [];
for (let d = 1; d <= daysInMonth; d++) {
  const anchor = new Date(Date.UTC(targetYear, targetMonth, d, 12, 0, 0));
  const weekday = weekdayFmt.format(anchor);

  if (weekday === 'Saturday') {
    const local = new Date(anchor.toLocaleString('en-US', { timeZone }));
    const y = local.getFullYear();
    const m = local.getMonth() + 1;
    const day = local.getDate();
    saturdays.push(`${y}-${pad(m)}-${pad(day)}`);
  }
}

// Output one item with all Saturdays in array + metadata
return [
  {
    json: {
      year: targetYear,
      month: targetMonth + 1,
      monthName,
      timeZone,
      saturdays
    }
  }
];

// n8n Code node
// Works with a field like items[0].json.Tasks = "Review Response 2025-09-06"

const input = items[0].json.Tasks || "";   // <-- use Tasks instead of text

const dateMatch = input.match(/\d{4}-\d{2}-\d{2}/);
const date = dateMatch ? dateMatch[0] : null;

// remove the date part to get only the text
const text = date ? input.replace(date, "").trim() : input;

return [
  {
    json: { text, date }
  }
];
