// Get current date
const now = new Date();

// Calculate last month (adjusting for January)
let year = now.getFullYear();
let month = now.getMonth() - 1;

if (month < 0) {
  month = 11;
  year -= 1;
}

// Start date of last month
const startDate = new Date(year, month, 1);

// End date of last month (0th day of current month = last day of previous)
const endDate = new Date(year, month + 1, 0);

// Extract parts
const pad = (num) => String(num).padStart(2, '0');

return [
  {
    json: {
      StartDay: pad(startDate.getDate()),
      StartMonth: pad(startDate.getMonth() + 1),
      StartYear: startDate.getFullYear(),

      EndDay: pad(endDate.getDate()),
      EndMonth: pad(endDate.getMonth() + 1),
      EndYear: endDate.getFullYear(),
    }
  }
];

// Get the input keywords
const keywordsRaw = $json["SEO Keywords"] || "";

// Split on commas, semicolons, newlines, or periods, then trim and filter
const keywordsArray = keywordsRaw
  .split(/[,;.\n]+/) // split on any of these delimiters
  .map(k => k.trim()) // remove extra spaces
  .filter(k => k.length > 0); // remove empty strings

// Return each keyword as a separate item
return keywordsArray.map(keyword => ({
  json: { keyword }
}));

// Build the merged "Keyword - Rank" text
const lines = items.map(item => `${item.json.Keyword} - Rank ${item.json["Ranking Position"]}`);

// Helper: get the first non-empty value for a field across rows
function firstNonEmpty(field) {
  for (const it of items) {
    const v = it.json[field];
    if (v !== undefined && v !== null) {
      const s = String(v).trim();
      if (s && s.toLowerCase() !== "[empty]") return v;
    }
  }
  return ""; // fallback if all are empty
}

const review = firstNonEmpty("Reviews");
const rating = firstNonEmpty("Rating");

return [
  {
    json: {
      textData: lines.join('\n'),
      review,   // single review value
      rating    // single rating value
    }
  }
];

// Get the AI Agent output text
const inputText = $json.output || "";

// Split into lines, clean them up
const lines = inputText
  .split("\n")
  .map(line => line.replace(/^\d+\.\s*/, "").trim()) // remove "1. "
  .filter(line => line.length > 0);

// Extract keyword + rank separately
const keywords = [];
const ranks = [];

lines.forEach(line => {
  // Match "... - Rank X" at the end
  const match = line.match(/^(.*?)-\s*Rank\s*(\d+)/i);
  if (match) {
    const keyword = match[1].trim();
    const rank = parseInt(match[2], 10);
    keywords.push(keyword);
    ranks.push(rank);
  }
});

// Return as single item with two arrays
return [
  {
    json: {
      keywords,
      ranks
    }
  }
];

const matches = [];

// Load Clients sheet
const clients = $('Get Clients').all().map(c => c.json);

// Loop through Apify scraped businesses
for (const item of $input.all()) {

  const data = item.json;
  const apiPlaceId = data.placeId;

  if (!apiPlaceId) continue;

  // Match using GMB Place ID
  const client = clients.find(c => {
    const clientPlaceId =
      c['GMB Place ID'] ||
      c.gmb_place_id ||
      c.placeId ||
      c.place_id;

    return clientPlaceId && clientPlaceId.toString() === apiPlaceId.toString();
  });

  if (!client) continue;

  matches.push({
    keyword: data.searchString,
    ranking_position: data.rank,
    client_id: client['Client ID'] || client.clientId || null,
    client_name: client['Client Name'] || client.name || null,
    whatsapp: client.WhatsApp || client.whatsapp || null,
    city: data.city,
    business_name: data.title,
    phone: data.phoneUnformatted || data.phone || null,
    website: data.website || null,
    rating: data.totalScore || null,
    reviews: data.reviewsCount || null,
    place_id: apiPlaceId,
    maps_url: data.url
  });
}

return matches;
