// Get current date
const now = new Date();

// Calculate last month (adjusting for January)
let year = now.getFullYear();
let month = now.getMonth() - 1;

if (month < 0) {
  month = 11;
  year -= 1;
}

// Start date of last month
const startDate = new Date(year, month, 1);

// End date of last month (0th day of current month = last day of previous)
const endDate = new Date(year, month + 1, 0);

// Extract parts
const pad = (num) => String(num).padStart(2, '0');

const startD = pad(startDate.getDate());
const startM = pad(startDate.getMonth() + 1);
const startY = startDate.getFullYear();

const endD = pad(endDate.getDate());
const endM = pad(endDate.getMonth() + 1);
const endY = endDate.getFullYear();

return [
  {
    json: {
      StartDay: startD,
      StartMonth: startM,
      StartYear: startY,
      StartDate_DB: `${startY}-${startM}-${startD}`, // Perfect for PostgreSQL (YYYY-MM-DD)

      EndDay: endD,
      EndMonth: endM,
      EndYear: endY,
      EndDate_DB: `${endY}-${endM}-${endD}` // Perfect for PostgreSQL (YYYY-MM-DD)
    }
  }
];
