// Collect all Keyword fields from input JSON
const input = $json;
const keywords = [];

for (let i = 1; i <= 10; i++) {
  const key = `Keyword ${i}`;
  if (input[key]) {
    keywords.push(input[key]);
  }
}

// Return each keyword as a separate item
return keywords.map(k => ({
  json: { keyword: k }
}));

