:root {
  --font-family: 'Inter', 'Segoe UI', Arial, sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 700;
  --font-size-body: 13px;
  --font-size-label: 15px;
  --font-size-input: 15px;
  --font-size-header: 22px;
  --font-size-subheader: 15px;

  /* Colors */
  --color-background: #23272f;
  --color-card-bg: #282d36;
  --color-card-border: #474e5d;
  --color-card-shadow: rgba(40, 45, 54, 0.12);

  --color-label: #bfc8d9;
  /* âœ¨ Make typed text dark so itâ€™s visible on white inputs */
  --color-input-text: #1f2937; /* dark gray / near black */
  --color-input-border: #474e5d;
  --color-focus-border: #4fd1c5;
  --color-submit-btn-bg: #4fd1c5;
  --color-submit-btn-text: #23272f;
  --color-error: #fc8181;
  --color-required: #fc8181;
  --color-link: #63b3ed;
  --color-header: #e2e8f0;
  --color-header-subtext: #8f99ab;

  /* Input visuals */
  --color-input-bg: #ffffff;        /* current inputs are white */
  --color-placeholder: #6b7280;     /* medium gray */

  /* Border Radii */
  --border-radius-card: 12px;
  --border-radius-input: 8px;
  --border-radius-clear-btn: 50%;
  --card-border-radius: 12px;

  /* Spacing */
  --padding-container-top: 32px;
  --padding-card: 28px;
  --padding-form-input: 14px;
  --card-margin-bottom: 20px;

  /* Dimensions */
  --container-width: 480px;
  --submit-btn-height: 50px;
  --checkbox-size: 20px;
  --opacity-placeholder: 0.85;
}

/* Card */
.form-card {
  background: var(--color-card-bg);
  border-radius: var(--border-radius-card);
  box-shadow: 0px 6px 20px 0px var(--color-card-shadow);
  padding: var(--padding-card);
  border: 1px solid var(--color-card-border);
  margin-bottom: var(--card-margin-bottom);
}

/* Labels */
.form-label {
  color: var(--color-label);
  font-size: var(--font-size-label);
  font-weight: var(--font-weight-bold);
}

/* Inputs */
.form-input,
.form-select {
  background: var(--color-input-bg);
  border: 1.5px solid var(--color-input-border);
  border-radius: var(--border-radius-input);
  color: var(--color-input-text);
  font-size: var(--font-size-input);
  padding: var(--padding-form-input);
  margin-bottom: 16px;
  transition: border 0.2s;
}

.form-input:focus, 
.form-select:focus {
  border-color: var(--color-focus-border);
  outline: none;
}

/* ðŸš© Force readable text in ALL native inputs inside the card (covers markup changes) */
.form-card input:not([type="checkbox"]):not([type="radio"]),
.form-card textarea,
.form-card select {
  background: var(--color-input-bg) !important;
  color: var(--color-input-text) !important;
  -webkit-text-fill-color: var(--color-input-text) !important; /* Safari/iOS */
  caret-color: var(--color-input-text) !important;
}

/* Placeholders */
.form-card input::placeholder,
.form-card textarea::placeholder {
  color: var(--color-placeholder) !important;
  opacity: 1 !important;
  font-size: var(--font-size-input);
}

/* Autofill fix (Chrome/Safari) */
.form-card input:-webkit-autofill,
.form-card textarea:-webkit-autofill {
  -webkit-text-fill-color: var(--color-input-text) !important;
  box-shadow: 0 0 0 1000px var(--color-input-bg) inset !important;
}

/* File input polish */
.form-card input[type="file"] {
  color: var(--color-placeholder);
}
.form-card input[type="file"]::-webkit-file-upload-button {
  background: var(--color-card-bg);
  color: var(--color-header);
  border: 1px solid var(--color-input-border);
  border-radius: 6px;
  padding: 8px 12px;
}

/* Submit */
.form-submit-button {
  background: var(--color-submit-btn-bg);
  color: var(--color-submit-btn-text);
  border: none;
  border-radius: var(--border-radius-input);
  height: var(--submit-btn-height);
  font-weight: var(--font-weight-bold);
  font-size: var(--font-size-label);
  cursor: pointer;
  transition: background 0.2s;
}
.form-submit-button:hover {
  background: #38b2ac;
}

/* Errors & links */
.form-error {
  color: var(--color-error);
  font-size: 13px;
  font-weight: var(--font-weight-bold);
}
.form-link {
  color: var(--color-link);
  text-decoration: underline;
  font-size: var(--font-size-body);
}

/* Branding removal */
a[href*="n8n.io"],
a[href*="n8n"] { display: none !important; }

:is(p, div, footer):has(> a[href*="n8n"]),
:is(p, div):has(> svg[aria-label="n8n"]) { display: none !important; }

.n8n-form-footer,
.form-footer,
