const results = [];

// Get Place ID from form
const formPlaceId = $('On form submission').first().json["GMB Place ID"]?.toString().trim();

if (!formPlaceId) {
  return [
    {
      json: {
        status: "not_found",
        reason: "place_id_missing"
      }
    }
  ];
}

let isFound = false;

// Loop through Apify scraped businesses
for (const item of $input.all()) {

  const data = item.json;
  const apiPlaceId = data.placeId;

  if (!apiPlaceId) continue;

  if (apiPlaceId.toString().trim() === formPlaceId) {

    isFound = true;

    results.push({
      status: "found",
      keyword: data.searchString,
      ranking_position: data.rank,
      business_name: data.title,
      category: data.categoryName,
      phone: data.phoneUnformatted || data.phone || null,
      website: data.website || null,
      rating: data.totalScore || null,
      reviews: data.reviewsCount || null,
      city: data.city,
      address: data.address,
      place_id: apiPlaceId,
      maps_url: data.url
    });
  }
}

// If no match found
if (!isFound) {
  return [
    {
      json: {
        status: "not_found",
        place_id: formPlaceId
      }
    }
  ];
}

// Return found business(es)
return results.map(r => ({ json: r }));
