// Collect all Keyword fields from input JSON
const input = $json;
const keywords = [];

for (let i = 1; i <= 10; i++) {
  const key = `Keyword ${i}`;
  if (input[key]) {
    keywords.push(input[key]);
  }
}

// Return each keyword as a separate item
return keywords.map(k => ({
  json: { keyword: k }
}));

// ---------- Inputs ----------
const formJson   = $item(0).$node['On form submission'].json;
const gmbPlaceId = formJson['GMB Place ID'] || formJson['gmb_place_id'] || null;

// Keyword always comes from Loop Over Keywords node
const loopJson   = $item(0).$node['Loop Over Keywords'].json;
const keyword    = loopJson.keyword || loopJson.Keyword || null;

// API results from HTTP node
const apiResults =
  Array.isArray($json?.data?.data) ? $json.data.data :
  Array.isArray($json?.data)       ? $json.data       : [];

// ---------- Rank match (1-based) ----------
const idx = apiResults.findIndex(r => {
  const pid = r?.place_id ?? r?.gmb_place_id ?? r?.placeId ?? null;
  return pid && String(pid) === String(gmbPlaceId);
});

const out = {
  keyword,
  gmb_place_id: gmbPlaceId,
  found: idx >= 0,
  rank: idx >= 0 ? idx + 1 : null,
};

if (idx >= 0) {
  const hit = apiResults[idx];
  out.match = {
    name:         hit.name ?? hit.title ?? null,
    address:      hit.full_address ?? hit.address ?? null,
    phone:        hit.phone_number ?? hit.phone ?? null,
    rating:       hit.rating ?? null,
    review_count: hit.review_count ?? hit.reviews ?? null,
    website:      hit.website ?? null,
    place_link:   hit.place_link ?? null,
    position_zero_based: idx,
  };
}

return [{ json: out }];

